import { Component, TplNode, Variant } from "@/wab/classes";
import { pinStateToPlasmicPinState } from "@/wab/client/components/variants/VariantRow";
import { Matcher } from "@/wab/client/components/view-common";
import Chip from "@/wab/client/components/widgets/Chip";
import { PlasmicVariantsDrawerRow } from "@/wab/client/plasmic/plasmic_kit_variants/PlasmicVariantsDrawerRow";
import { ensure, maybe } from "@/wab/common";
import { getNamespacedComponentName } from "@/wab/components";
import { VariantPinState } from "@/wab/shared/PinManager";
import {
  isStandaloneVariant,
  isStyleVariant,
  isVariantSettingEmpty,
  tryGetVariantSetting,
} from "@/wab/shared/Variants";
import { isTplVariantable } from "@/wab/tpls";
import { observer } from "mobx-react";
import * as React from "react";

interface VariantsDrawerRowProps {
  variant: Variant;
  pinState: VariantPinState | undefined;
  isFocused?: boolean;
  matcher: Matcher;
  onToggle?: () => void;
  onTarget?: (target: boolean) => void;
  focusedTpl?: TplNode;
  superComp?: Component;
}

const VariantsDrawerRow = observer(function VariantsDrawerRow(
  props: VariantsDrawerRowProps
) {
  const {
    variant,
    pinState,
    isFocused,
    matcher,
    onToggle,
    onTarget,
    focusedTpl,
    superComp,
  } = props;
  const indicatedVs =
    focusedTpl &&
    isTplVariantable(focusedTpl) &&
    maybe(tryGetVariantSetting(focusedTpl, [variant]), (vs) =>
      isVariantSettingEmpty(vs) ? undefined : vs
    );

  const isRecording = pinState?.startsWith("selected");
  return (
    <PlasmicVariantsDrawerRow
      isFocused={isFocused}
      pinState={pinStateToPlasmicPinState(pinState)}
      isIndicated={!!indicatedVs}
      variantPinButton={
        onToggle
          ? {
              onToggle,
              tabIndex: isFocused ? 0 : -1,
            }
          : { render: () => null }
      }
      recordButton={
        onTarget
          ? {
              tooltip: isRecording ? "Stop recording" : "Add to recording",
              onClick: (e) => {
                e.stopPropagation();
                onTarget(!isRecording);
              },
              tabIndex: isFocused ? 0 : -1,
            }
          : { render: () => null }
      }
      isStandalone={isStandaloneVariant(variant)}
    >
      {isStyleVariant(variant) ? (
        ensure(variant.selectors).map((sel) => (
          <Chip key={sel}>{matcher.boldSnippets(sel)}</Chip>
        ))
      ) : superComp && isStandaloneVariant(variant) ? (
        <span>
          {getNamespacedComponentName(superComp)}
          {" - "}
          {matcher.boldSnippets(variant.name)}
        </span>
      ) : (
        matcher.boldSnippets(variant.name)
      )}
    </PlasmicVariantsDrawerRow>
  );
});

export default VariantsDrawerRow;
